mapper.select:
SELECT *
  FROM (SELECT ROWNUM R_NUM, ATT.*
          FROM (SELECT *
                  FROM ATTENDANCE
                  WHERE AVAILABLE = 1
                 ORDER BY A_IDX DESC
               ) ATT
       )
 WHERE R_NUM BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE ATTENDANCE 
    SET AVAILABLE = 0
    WHERE A_IDX = #{a_idx}
